mapper.select:
select
`id`,`from_path`,`to_hostport`,`to_path`,`service_id`,`rpc`,`gmt_create`,`gmt_modified`
from gateway_route

	 and id = #{id} 
	 and from_path = #{fromPath} 
	 and to_hostport = #{toHostport} 
	 and to_path = #{toPath} 
	 and service_id = #{serviceId} 
	 and rpc = #{rpc} 



		order by ${sort} ${order}


		order by id desc



	limit #{offset}, #{limit}

select count(*) from gateway_route

	 and id = #{id} 
	 and from_path = #{fromPath} 
	 and to_hostport = #{toHostport} 
	 and to_path = #{toPath} 
	 and service_id = #{serviceId} 
	 and rpc = #{rpc}

mapper.update:
update gateway_route

	 from_path = #{fromPath} 
	 to_hostport = #{toHostport} 
	 to_path = #{toPath} 
	 service_id = #{serviceId} 
	 rpc = #{rpc} 
	`gmt_modified`=sysdate()

where id = #{id}
